import React, { useEffect } from 'react';
import {
	Container,
	Typography,
	Box,
	Grid,
	Paper,
	Button,
	IconButton,
} from '@mui/material';
import { useNavigate, useLocation } from 'react-router-dom';
import MedicalServicesIcon from '@mui/icons-material/MedicalServices';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import PhoneIcon from '@mui/icons-material/Phone';
import EmailIcon from '@mui/icons-material/Email';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import LocalHospitalIcon from '@mui/icons-material/LocalHospital';
import HealingIcon from '@mui/icons-material/Healing';
import ChildCareIcon from '@mui/icons-material/ChildCare';
import ElderlyIcon from '@mui/icons-material/Elderly';
import VaccinesIcon from '@mui/icons-material/Vaccines';

function Homepage() {
	const navigate = useNavigate();
	const location = useLocation();

	useEffect(() => {
		if (location.state?.scrollTo) {
			const element = document.getElementById(location.state.scrollTo);
			if (element) {
				element.scrollIntoView({ behavior: 'smooth' });
			}
		}
	}, [location]);

	const services = [
		{
			icon: (
				<MedicalServicesIcon
					sx={{ fontSize: 50, color: 'secondary.main', mb: 2 }}
				/>
			),
			title: 'General Health Checkup',
			description:
				'Comprehensive health screening and preventive care services',
		},
		{
			icon: (
				<LocalHospitalIcon
					sx={{ fontSize: 50, color: 'secondary.main', mb: 2 }}
				/>
			),
			title: 'Dental Care',
			description:
				'Professional dental services including cleaning, filling, and orthodontics',
		},
		{
			icon: (
				<HealingIcon sx={{ fontSize: 50, color: 'secondary.main', mb: 2 }} />
			),
			title: 'Physiotherapy',
			description:
				'Rehabilitation services for injuries and chronic conditions',
		},
		{
			icon: (
				<ChildCareIcon sx={{ fontSize: 50, color: 'secondary.main', mb: 2 }} />
			),
			title: 'Pediatric Care',
			description: 'Specialized healthcare services for children and infants',
		},
		{
			icon: (
				<ElderlyIcon sx={{ fontSize: 50, color: 'secondary.main', mb: 2 }} />
			),
			title: 'Geriatric Care',
			description: 'Dedicated healthcare services for elderly patients',
		},
		{
			icon: (
				<VaccinesIcon sx={{ fontSize: 50, color: 'secondary.main', mb: 2 }} />
			),
			title: 'Vaccination',
			description: 'Complete range of vaccines for all age groups',
		},
	];

	return (
		<Box sx={{ overflow: 'auto', pt: 8 }}>
			{/* Hero Section */}
			<Box
				id="home"
				sx={{
					bgcolor: 'primary.main',
					color: 'primary.contrastText',
					py: 15,
					textAlign: 'center',
					position: 'relative',
					minHeight: '92vh',
					display: 'flex',
					flexDirection: 'column',
					justifyContent: 'center',
					background: 'linear-gradient(45deg, #1A1A1A 30%, #2C2C2C 90%)',
					boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
				}}
			>
				<Container maxWidth="md">
					<Typography
						variant="h1"
						component="h1"
						gutterBottom
						sx={{
							fontSize: { xs: '2.5rem', md: '3.5rem' },
							mb: 3,
							textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
						}}
					>
						Welcome to Primer Cherang Clinic
					</Typography>
					<Typography
						variant="h5"
						component="h2"
						gutterBottom
						sx={{
							fontSize: { xs: '1.2rem', md: '1.5rem' },
							mb: 5,
							color: 'rgba(255,255,255,0.9)',
							fontWeight: 400,
						}}
					>
						Your Health is Our Priority
					</Typography>
					<Button
						variant="contained"
						color="secondary"
						size="large"
						sx={{
							py: 2,
							px: 6,
							fontSize: '1.1rem',
							fontWeight: 500,
							'&:hover': {
								transform: 'translateY(-3px)',
								boxShadow: '0 6px 20px rgba(220,38,38,0.4)',
							},
						}}
						onClick={() => navigate('/booking')}
					>
						Book Appointment
					</Button>
				</Container>
				<IconButton
					sx={{
						position: 'absolute',
						bottom: 40,
						left: '50%',
						transform: 'translateX(-50%)',
						color: 'white',
						animation: 'bounce 2s infinite',
						'@keyframes bounce': {
							'0%, 100%': {
								transform: 'translateX(-50%) translateY(0)',
							},
							'50%': {
								transform: 'translateX(-50%) translateY(10px)',
							},
						},
					}}
					onClick={() =>
						document
							.getElementById('services')
							.scrollIntoView({ behavior: 'smooth' })
					}
				>
					<KeyboardArrowDownIcon sx={{ fontSize: 48 }} />
				</IconButton>
			</Box>

			{/* Services Section */}
			<Box
				id="services"
				sx={{
					py: 12,
					bgcolor: 'background.default',
					position: 'relative',
				}}
			>
				<Container maxWidth="lg">
					<Typography
						variant="h2"
						component="h2"
						gutterBottom
						align="center"
						color="primary"
						sx={{ mb: 6 }}
					>
						Our Services
					</Typography>
					<Grid container spacing={4}>
						{services.map((service, index) => (
							<Grid item xs={12} md={4} key={index}>
								<Paper
									sx={{
										p: 4,
										height: '100%',
										textAlign: 'center',
										transition: 'all 0.3s ease-in-out',
										'&:hover': {
											transform: 'translateY(-10px)',
											boxShadow: '0 8px 24px rgba(0,0,0,0.12)',
											bgcolor: 'background.paper',
										},
										display: 'flex',
										flexDirection: 'column',
										alignItems: 'center',
									}}
								>
									<Box
										sx={{
											bgcolor: 'primary.light',
											borderRadius: '50%',
											p: 2,
											mb: 3,
											display: 'flex',
											alignItems: 'center',
											justifyContent: 'center',
										}}
									>
										{service.icon}
									</Box>
									<Typography
										variant="h5"
										gutterBottom
										sx={{
											fontWeight: 600,
											color: 'primary.main',
											mb: 2,
										}}
									>
										{service.title}
									</Typography>
									<Typography color="text.secondary" sx={{ lineHeight: 1.7 }}>
										{service.description}
									</Typography>
								</Paper>
							</Grid>
						))}
					</Grid>
				</Container>
			</Box>

			{/* Contact Section */}
			<Box
				id="contact"
				sx={{
