import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import {
	Container,
	Typography,
	Box,
	Grid,
	Paper,
	Button,
	IconButton,
} from '@mui/material';
import MedicalServicesIcon from '@mui/icons-material/MedicalServices';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import PhoneIcon from '@mui/icons-material/Phone';
import EmailIcon from '@mui/icons-material/Email';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import LocalHospitalIcon from '@mui/icons-material/LocalHospital';
import HealingIcon from '@mui/icons-material/Healing';
import ChildCareIcon from '@mui/icons-material/ChildCare';
import ElderlyIcon from '@mui/icons-material/Elderly';
import VaccinesIcon from '@mui/icons-material/Vaccines';
import BookingModal from './Booking';

function Homepage() {
	const location = useLocation();
	const [openBooking, setOpenBooking] = useState(false);

	useEffect(() => {
		if (location.state?.scrollTo) {
			const element = document.getElementById(location.state.scrollTo);
			if (element) {
				element.scrollIntoView({ behavior: 'smooth' });
			}
		}
	}, [location]);

	const services = [
		{
			icon: (
				<MedicalServicesIcon
					sx={{ fontSize: 50, color: 'secondary.main', mb: 2 }}
				/>
			),
			title: 'General Health Checkup',
			description:
				'Comprehensive health screening and preventive care services',
		},
		{
			icon: (
				<LocalHospitalIcon
					sx={{ fontSize: 50, color: 'secondary.main', mb: 2 }}
				/>
			),
			title: 'Dental Care',
			description:
				'Professional dental services including cleaning, filling, and orthodontics',
		},
		{
			icon: (
				<HealingIcon sx={{ fontSize: 50, color: 'secondary.main', mb: 2 }} />
			),
			title: 'Physiotherapy',
			description:
				'Rehabilitation services for injuries and chronic conditions',
		},
		{
			icon: (
				<ChildCareIcon sx={{ fontSize: 50, color: 'secondary.main', mb: 2 }} />
			),
			title: 'Pediatric Care',
			description: 'Specialized healthcare services for children and infants',
		},
		{
			icon: (
				<ElderlyIcon sx={{ fontSize: 50, color: 'secondary.main', mb: 2 }} />
			),
			title: 'Geriatric Care',
			description: 'Dedicated healthcare services for elderly patients',
		},
		{
			icon: (
				<VaccinesIcon sx={{ fontSize: 50, color: 'secondary.main', mb: 2 }} />
			),
			title: 'Vaccination',
			description: 'Complete range of vaccines for all age groups',
		},
	];

	const handleBookingClick = () => {
		setOpenBooking(true);
	};

	return (
		<Box sx={{ overflow: 'auto', pt: 8 }}>
			{/* Hero Section */}
			<Box
				id="home"
				sx={{
					position: 'relative',
					minHeight: '92vh',
					display: 'flex',
					flexDirection: 'column',
					justifyContent: 'center',
					background: 'linear-gradient(135deg, #000000 0%, #1A1A1A 100%)',
					'&::before': {
						content: '""',
						position: 'absolute',
						top: 0,
						left: 0,
						right: 0,
						bottom: 0,
						background: 'url(/hero-bg.jpg) center/cover',
						opacity: 0.1,
						zIndex: 1,
					},
				}}
			>
				<Container
					maxWidth="md"
					sx={{
						position: 'relative',
						zIndex: 2,
						textAlign: 'center',
						py: { xs: 8, md: 15 },
					}}
				>
					<Typography
						variant="h1"
						component="h1"
						gutterBottom
						sx={{
							fontSize: { xs: '2.5rem', md: '4rem' },
							fontWeight: 800,
							color: 'white',
							textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
							mb: 4,
							lineHeight: 1.2,
						}}
					>
						Welcome to <br />
						<Box
							component="span"
							sx={{
								color: 'secondary.main',
								display: 'block',
								mt: 1,
							}}
						>
							Primer Cherang Clinic
						</Box>
					</Typography>
					<Typography
						variant="h5"
						component="h2"
						gutterBottom
						sx={{
							fontSize: { xs: '1.2rem', md: '1.5rem' },
							mb: 6,
							color: 'rgba(255,255,255,0.9)',
							fontWeight: 400,
							maxWidth: '800px',
							mx: 'auto',
							lineHeight: 1.6,
						}}
					>
						Your Health is Our Priority. Experience compassionate care with our
						team of dedicated healthcare professionals.
					</Typography>
					<Box sx={{ display: 'flex', gap: 3, justifyContent: 'center' }}>
						<Button
							variant="contained"
							color="secondary"
							size="large"
							sx={{
								py: 2,
								px: 6,
								fontSize: '1.1rem',
								fontWeight: 600,
								borderRadius: '50px',
								'&:hover': {
									transform: 'translateY(-3px)',
									boxShadow: '0 6px 20px rgba(220,38,38,0.4)',
								},
							}}
							onClick={handleBookingClick}
						>
							Book Appointment
						</Button>
						<Button
							variant="outlined"
							color="inherit"
							size="large"
							sx={{
								py: 2,
								px: 6,
								fontSize: '1.1rem',
								fontWeight: 600,
								borderRadius: '50px',
								borderColor: 'rgba(255,255,255,0.5)',
								color: 'white',
								'&:hover': {
									borderColor: 'white',
									backgroundColor: 'rgba(255,255,255,0.1)',
								},
							}}
							onClick={() =>
								document
									.getElementById('services')
									.scrollIntoView({ behavior: 'smooth' })
							}
						>
							Our Services
						</Button>
					</Box>
				</Container>
				<IconButton
					sx={{
						position: 'absolute',
						bottom: 40,
						left: '50%',
						transform: 'translateX(-50%)',
						color: 'white',
						zIndex: 2,
						animation: 'bounce 2s infinite',
						'@keyframes bounce': {
							'0%, 100%': {
								transform: 'translateX(-50%) translateY(0)',
							},
							'50%': {
								transform: 'translateX(-50%) translateY(10px)',
							},
						},
					}}
					onClick={() =>
						document
							.getElementById('services')
							.scrollIntoView({ behavior: 'smooth' })
					}
				>
					<KeyboardArrowDownIcon sx={{ fontSize: 48 }} />
				</IconButton>
			</Box>

			{/* Services Section */}
			<Box
				id="services"
				sx={{
					py: 12,
					bgcolor: 'background.default',
					position: 'relative',
				}}
			>
				<Container maxWidth="lg">
					<Typography
						variant="h2"
						component="h2"
						gutterBottom
						align="center"
						color="primary"
						sx={{ mb: 6 }}
					>
						Our Services
					</Typography>
					<Grid container spacing={4}>
						{services.map((service, index) => (
							<Grid item xs={12} md={4} key={index}>
								<Paper
									sx={{
										p: 4,
										height: '100%',
										textAlign: 'center',
										transition: 'all 0.3s ease-in-out',
										'&:hover': {
											transform: 'translateY(-10px)',
											boxShadow: '0 8px 24px rgba(0,0,0,0.12)',
											bgcolor: 'background.paper',
										},
										display: 'flex',
										flexDirection: 'column',
										alignItems: 'center',
									}}
								>
									<Box
										sx={{
											bgcolor: 'primary.light',
											borderRadius: '50%',
											p: 2,
											mb: 3,
											display: 'flex',
											alignItems: 'center',
											justifyContent: 'center',
										}}
									>
										{service.icon}
									</Box>
									<Typography
										variant="h5"
										gutterBottom
										sx={{
											fontWeight: 600,
											color: 'primary.main',
											mb: 2,
										}}
									>
										{service.title}
									</Typography>
									<Typography color="text.secondary" sx={{ lineHeight: 1.7 }}>
										{service.description}
									</Typography>
								</Paper>
							</Grid>
						))}
					</Grid>
				</Container>
			</Box>

			{/* Contact Section */}
			<Box
				id="contact"
				sx={{
					py: 12,
					bgcolor: 'primary.light',
					color: 'white',
					position: 'relative',
				}}
			>
				<Container maxWidth="lg">
					<Typography
						variant="h2"
						component="h2"
						gutterBottom
						align="center"
						sx={{ mb: 6 }}
					>
						Contact Us
					</Typography>
					<Grid container spacing={4}>
						<Grid item xs={12} md={4}>
							<Paper
								sx={{
									p: 4,
									height: '100%',
									transition: 'all 0.3s ease',
									'&:hover': {
										transform: 'translateY(-5px)',
										boxShadow: '0 8px 24px rgba(0,0,0,0.15)',
									},
								}}
							>
								<LocationOnIcon
									sx={{ fontSize: 40, color: 'secondary.main', mb: 2 }}
								/>
								<Typography variant="h6" gutterBottom>
									Location
								</Typography>
								<Typography>
									123 Cherang Street
									<br />
									Kuala Lumpur, 50450
									<br />
									Malaysia
								</Typography>
							</Paper>
						</Grid>
						<Grid item xs={12} md={4}>
							<Paper
								sx={{
									p: 4,
									height: '100%',
									transition: 'all 0.3s ease',
									'&:hover': {
										transform: 'translateY(-5px)',
										boxShadow: '0 8px 24px rgba(0,0,0,0.15)',
									},
								}}
							>
								<PhoneIcon
									sx={{ fontSize: 40, color: 'secondary.main', mb: 2 }}
								/>
								<Typography variant="h6" gutterBottom>
									Phone
								</Typography>
								<Typography>
									+60 3-1234 5678
									<br />
									Emergency: +60 3-1234 5679
								</Typography>
							</Paper>
						</Grid>
						<Grid item xs={12} md={4}>
							<Paper
								sx={{
									p: 4,
									height: '100%',
									transition: 'all 0.3s ease',
									'&:hover': {
										transform: 'translateY(-5px)',
										boxShadow: '0 8px 24px rgba(0,0,0,0.15)',
									},
								}}
							>
								<EmailIcon
									sx={{ fontSize: 40, color: 'secondary.main', mb: 2 }}
								/>
								<Typography variant="h6" gutterBottom>
									Email
								</Typography>
								<Typography>
									info@primercherang.com
									<br />
									support@primercherang.com
								</Typography>
							</Paper>
						</Grid>
					</Grid>
				</Container>
			</Box>

			<BookingModal open={openBooking} onClose={() => setOpenBooking(false)} />
		</Box>
	);
}

export default Homepage;
